import React, { useEffect, useState } from 'react';
import { connect } from "react-redux";
import ApiRequest from '../../services/api/Api-request';
import { Loading } from '../Loading/Loading';
import { VideoCard } from '../video/VideoCard';
import './Main.css';

const Main = (props) => {

    const [videoData, setVideoData] = useState(props.state.contents);
    useEffect(() => {
        const params = new URLSearchParams();
        params.append("token", props.state.token);
        params.append("device", "Web");
        props.main(params);
    }, [])

    const changeFav = () =>{

    }

    return (
        <div>
            <div class="header">
                <a href="#default" class="logo">CompanyLogo</a>
                <div class="header-right">
                    <a class="active" href="#home">Home</a>
                    <a href="#contact">Contact</a>
                    <a href="#about">About</a>
                </div>
            </div>
            <div className="card-grid">
                {
                    setVideoData()
                }
                {
                    videoData.map(content => (
                        <VideoCard key={content.id} videoData={content} />
                    ))
                }
                {props.state.loading &&
                    <Loading />
                }
            </div>
        </div>
    )
}

const mapStateToProps = (state) => ({ state: state });

const mapDispatchToProps = (dispatch) => ({
    main: (params) => {
        ApiRequest.App.Main(params)(dispatch);
    },
});

const connectedMain = connect(
    mapStateToProps,
    mapDispatchToProps
)(Main);

export default connectedMain;
