import md5 from 'md5';
import React, { useState } from 'react'
import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import ApiRequest from "../../services/api/Api-request";
import './Login.css';


const Login = (props) =>{
    const [user, setUser] = useState('')
    const [pass, setPass] = useState('')

    const onSubmit = (event) => {
        event.preventDefault();
        const params = new URLSearchParams();
        params.append("user", user);
        params.append("pass", md5(pass));
        params.append("device", "Web");
        props.login(params);      
      };

      const handleChangePass = (event) => {
        setPass(event.target.value);
      }

      const handleChangeUser = (event) => {
        setUser(event.target.value);
      }

    return (
        <div>
            <form onSubmit={onSubmit}>
                <label htmlFor="user">User</label>
                <input type="email" name="user" id="user" value={user} onChange={handleChangeUser}/>
                <label htmlFor="pass" >Password</label>
                <input type="password" name="pass" id="pass" value={pass} onChange={handleChangePass}/>
                <button type="submit">Entrar</button>
            </form>

            <Link to="/main">About</Link>
        </div>
    )
}


const mapStateToProps = (state) => ({ state: state });

const mapDispatchToProps = (dispatch) => ({
    login: (params) => {
    ApiRequest.Session.Login(params)(dispatch);
  },
});

const connectedLogin = connect(
  mapStateToProps,
  mapDispatchToProps
)(Login);

export default connectedLogin;
