const initialState = {
    taskList: [],
    tasks: [],
    favs: [],
    login: false,
};

function reducer(state = initialState, action) {
    switch (action.type) {
        //add new list
        case "ADD_TASKLIST":
            return {
                ...state,
                taskList: [
                    ...state.taskList,
                    {
                        id: action.id,
                        tittle: action.tittle,
                        tasks: [], 
                    },
                ],
            };
        //add task to list
        case "ADD_TASK":
            return {
                ...state,
                taskList: [
                  ...addTaskToList(state.taskList, action),
                ]
            };
        default:
            return state;
    }
}

function addTaskToList(taskList, action){
    console.log(action)
    taskList[action.taskListId].tasks.push({
        id : action.id,
        text: action.text
    })
    return taskList;
}
export default reducer;
