const initialState = {
    user: {},
    contents: [],
    login: false,
};

function reducer(state = initialState, {type, data}) {
    switch (type) {
        //add new list
        case "ADD_TASKLIST":
            return {
                ...state,
                taskList: [
                    ...state.taskList,
                    {
                        id: action.id,
                        tittle: action.tittle,
                        tasks: [], 
                    },
                ],
            };
        //add task to list
        case "INIT_MAIN":
            return {
                ...state,
                contents: data.contents,
                user: data.user,

            };
        default:
            return state;
    }
}

export default reducer;
