import Config from "../config/config";


const initialState = {
    user: {},
    contents: [],
    login: false,
    token: ''
};

function reducer(state = initialState, {type, data}) {
    console.log('peine')
    switch (type) {
        //add new list
        case "LOGIN":

            return {
                ...state,
                token: data.token,
            };
        //add task to list
        case "INIT_MAIN":
            return {
                ...state,
                contents: data.contents,
                user: data.user,

            };
        default:
            return state;
    }
}

export default reducer;
